import logo from './images/amazon-logo.png';
import styleClasses from './App.module.css';
import productData from './ProductData';
import pProductPreview from './productPreview';

function App() {
  let today = new Date();
  const currentHour = today.getHours() < 9 ? '0' + today.getHours() : today.getHours();
  const currentMinute = today.getMinutes() < 9 ? '0' + today.getMinutes() : today.getMinutes();

  return (
    <div className="App">
      <header className={styleClasses.topBar}>
        <nav>
          <img src={logo} alt="logo" />
        </nav>
      </header>
      <main>
        <div className={styleClasses.mainContainer}>
          <pProductPreview />
          <div className={styleClasses.infoContainer}>
            <h1 className={styleClasses.productHeading}>{productData.title}</h1>
            <p className={styleClasses.productDescription}>{productData.description}</p>
            <h3 className={styleClasses.colorSelectionHeading}>Select Color</h3>
            <div className={styleClasses.colorSelectionContainer}>
              <img className={[styleClasses.colorSelectionProductImage, styleClasses.selectedProductImage].join(' ')} src={productData.colorOptions[0].imageUrl} alt='black' />
              <img className={styleClasses.colorSelectionProductImage} src={productData.colorOptions[1].imageUrl} alt='red' />
              <img className={styleClasses.colorSelectionProductImage} src={productData.colorOptions[2].imageUrl} alt='blue' />
              <img className={styleClasses.colorSelectionProductImage} src={productData.colorOptions[3].imageUrl} alt='brown' />
            </div>
            <h3 className={styleClasses.featureSelectionHeading}>Features</h3>
            <div className={styleClasses.featureContainer}>
              <button className={[styleClasses.timeButton, styleClasses.selectedFeature].join(' ')}>{productData.featureList[0]}</button>
              <button className={styleClasses.heartrateButton}>{productData.featureList[1]}</button>
            </div>
            <button className={styleClasses.buyBtn}>Buy Now</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
