import styleClasses from './App.module.css';
import ProductPreview from './productPreview/productPreview';
import ProductInfo from './productInfo/productInfo';
import TopBar from './topBar/topBar';
import ProductData from './utilities/ProductData';
import { Component } from 'react';

class App extends Component {

  state = {
    productData: ProductData,
    currentPreviewImage: 'https://imgur.com/iOeUBV7.png',
    showHeartrate: false
  };

  onColorOptionClick = (pos) => {
    const updatedCurrentPreviewImage = this.state.productData.colorOptions[pos].imageUrl;
    this.setState({currentPreviewImage: updatedCurrentPreviewImage}); 
  };
  render() {
    return (
      <div className="App">
        <TopBar />
        <main>
          <div className={styleClasses.mainContainer}>
            <ProductPreview currentPreviewImage={this.state.currentPreviewImage} showHeartrate={this.state.showHeartrate} onColorOptionClick={this.onColorOptionClick}/>
            <ProductInfo data={this.state.productData} />
          </div>
        </main>
      </div>
    );
  }
}

export default App;
